#include "BigInteger.h"
#include <iostream>
#include <minwindef.h>
#include <sysinfoapi.h>

using namespace std;

int main() {
    int m = 500;
    DWORD start, end;

    //region 测试
    //测试大数加法
    start = GetTickCount();
    BigInteger integer0 = BigInteger::addMod(
            "",
            "",
            m).trim();
    end = GetTickCount();
    integer0.print();
    cout << end << "-" << start << " = " << end - start << " ms" << endl;

    //测试大数减法
    start = GetTickCount();
    BigInteger integer1 = BigInteger::subMod(
            "",
            "",
            m).trim();
    end = GetTickCount();
    integer1.print();
    cout << end << "-" << start << " = " << end - start << " ms" << endl;

    //测试大数乘法
    start = GetTickCount();
    BigInteger integer2 = BigInteger::multiMod(
            "",
            "",
            m).trim();
    end = GetTickCount();
    integer2.print();
    cout << end << "-" << start << " = " << end - start << " ms" << endl;

    //测试大数除法
    start = GetTickCount();
    BigInteger integer3 = BigInteger::divideMod(
            "",
            "",
            m).trim();
    end = GetTickCount();
    integer3.print();
    cout << end << "-" << start << " = " << end - start << " ms" << endl;

    //测试大数乘幂
    start = GetTickCount();
        BigInteger integer4 = BigInteger::powMod(
            "",
            "",
            m).trim();
    end = GetTickCount();
    integer4.print();
    cout << end << "-" << start << " = " << end - start << " ms" << endl;
    //endregion

    return 0;
}